import {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {detailsOrder} from "../action/orderActions";
import {detailsUser, updateUserProfile} from "../action/userActions";
import LoadingBox from "../share/LoadingBox";
import MessageBox from "../share/MessageBox";

export default function ProfileScreen(){
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')

    const userSignin = useSelector((state) => state.userSignin)
    const { userinfo } = userSignin
    const userDetails = useSelector((state) => state.userDetails)
    const {loading, error, user} = userDetails

    const dispatch = useDispatch()

    useEffect(() =>{
        if(!user){
            dispatch(detailsUser(userinfo._id))
        }else {
            setName(user.name)
            setEmail(user.email)
        }

    },[dispatch, userinfo._id])
    const submitHandler = (e) =>{
        e.preventDefault()
        //TODO: dispatch upadete profile
        if(password !== confirmPassword){
            alert('Password and Confirm Password are not match')
        }else {
            dispatch(updateUserProfile({userId: user._id, name,email,password}))
        }
    }
    return(
        <div>
            <form className="form" onSubmit={submitHandler}>
                <div>
                    <h1>User Profile</h1>
                </div>
                {
                    loading ? <LoadingBox></LoadingBox>
                    : error ? <MessageBox>{error}</MessageBox>
                    :
                    <>
                        <div>
                            <label htmlFor="name">Name</label>
                            <input
                                id="name"
                                type="text"
                                placeholder="Enter name"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            ></input>
                        </div>
                        <div>
                            <label htmlFor="email">Name</label>
                            <input
                                id="email"
                                type="text"
                                placeholder="Enter email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            ></input>
                        </div>
                        <div>
                            <label htmlFor="password">Password</label>
                            <input
                                id="password"
                                type="text"
                                placeholder="Enter Password"
                                onChange={(e) => setPassword(e.target.value)}
                            ></input>
                        </div>
                        <div>
                            <label htmlFor="confirmpassword">Confrim Password</label>
                            <input
                                id="confirmpassword"
                                type="text"
                                placeholder="Enter confirm password"
                                onChange={(e) => setConfirmPassword(e.target.value)}
                            ></input>
                        </div>
                        <label />
                        <button className="primary" type="submit">
                            Update
                        </button>
                    </>

                }
            </form>
        </div>
    )
}
